// Custom Scroll for Sidebar
$(function() {
	$('.sidebar-content').slimScroll({
		color: 'rgba(0, 0, 0, 0.1)',
		alwaysVisible: false,
		size: "4px",
		height: "100%",
		distance: '1px',
		railVisible: false,
		railColor: "#0066bf",
		position: 'right',
	});
});


